import './App.css';
import socketio from 'socket.io-client';
import React, { useEffect, useState } from 'react';

const sendMessage = (to, data) => {
	console.log(to)
	console.log(data)
}

function App() {

	const [socket, setSocket] = useState(null);
	const [message, setMessage] = useState("")

	useEffect(() => {
		const newSocket = socketio(`http://localhost:5000`);
		setSocket(newSocket);
		console.log("connected")
		return () => newSocket.close();
	}, [setSocket]);

	const handleMessage = (event) => {
		setMessage(event.target.value)

	}
	return (
		<div className="App">

			<p>Inserire un messaggio per Client2</p>

			<input type="text" onChange={ handleMessage }></input>

			<button onClick={ sendMessage("client2", message) }>SEND MESSAGE</button>


		</div>
	);
}



export default App;
