import './App.css';
import socketio from 'socket.io-client';
import React, { useEffect, useState } from 'react';


function App() {

	const [socket, setSocket] = useState(null);
	const [message, setMessage] = useState("")

	useEffect(() => {
		const newSocket = socketio(`http://localhost:5000`);
		setSocket(newSocket);
		console.log("connected")
		return () => newSocket.close();
	}, [setSocket]);

	handleMessage = (event) => {
		setMessage(event.target.value)
	}

	sendMessage = (to, data) => {
		console.log(to)
		console.log(data)
	}

	return (
		<div className="App">

			<p>Inserire un messaggio per Client2</p>

			<input type="text" onChange={ this.handleMessage() }></input>

			<button onClick={ this.sendMessage("client2", message) }>SEND MESSAGE</button>


		</div>
	);
}



export default App;
